const initialState = {
    user: {
        isLoggedIn: false,
        user: null,
        signUpData: {},
        loginData: {},
    },
    post: {
        mainPosts: [],
    },
};

export const loginAction = (data) => {
    return {
        type: 'LOG_IN',
        data,
    }
};

export const logoutAction = () => {
    return {
        type: 'LOG_OUT',
    }
};

//action creator
//액션 크리에이터를 하면 아래에 있는 것처럼 아주 쉽게 반복해서 사용할 수 있기 때문에 좋음.
//async action crator(비동기 액션 크리에이터도 만들 수 있는데 그건 redux saga를 이용)
// const changeNickname = (data) => {
//     return {
//         type: 'CHANGE_NICKNAME',
//         data,
//     }
// }
// changeNickname('dabin');
//이건 위와 같음
//{
// type: 'CHANGE_NICKNAME',
// data: 'dabin',
//}

// store.dispatch(changeNickname('닉네임'))

// (이전상태, 액션) => 다음상태 이게 리덕스
const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'LOG_IN':
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: true,
                    user: action.data,
                },
            }
        case 'LOG_OUT':
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: false,
                    user: null,
                },
            }
    }
};

export default rootReducer;
